          </View>

          {/* Growth Stage Selection */}
          <View className="mt-6">
            <Text className="text-sm font-medium text-neutral-700 dark:text-neutral-300 mb-3">
              Growth Stage
            </Text>
            <ScrollView horizontal showsHorizontalScrollIndicator={false}>
